mapper.select:
select role_id
from user_tb
where account = #{account}

select student_tb.sname
from student_tb,
     user_tb
where student_tb.sno = user_tb.account
  and user_tb.account = #{account}

mapper.update:
update user_tb
set password=#{password},
    private_salt=#{private_salt}
where account = #{account}
